import { Component, Input, OnInit } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import html2canvas from 'html2canvas';
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-Sesiones',
  templateUrl: './Sesiones.component.html',
  styleUrls: ['./Sesiones.component.scss']
})
export class SesionesComponent implements OnInit {

  createImage;
  create = false;
  ocultar = false;
  name: string;
  description: string;
  dimensions: string;
  objectives: string;
  duration: string;

  isEnable: boolean = true;

  constructor() { }

  ngOnInit() {
  }

  createPicture() {
    if (this.isEnable === true) {
      // Recogemos el contenedor que queremos convertir a imagen
      html2canvas(document.querySelector("#divPDF")).then(canvas => {
        // Convertimos el canvas a imagen
        this.createImage = canvas.toDataURL();

      });
      this.create = true;
      this.isEnable = false;
      // Retornamos la variable donde la hemos guardado
      return this.createImage;
    }
  }

  resetImage() {
    this.create = false;
    this.isEnable = true;
    this.createPicture();
  }

  createPDF() {
    const pdfDefinition: any = {
      info:{
        title: this.name,
      },
      pageOrientation: 'landscape',
      content: [
        {
          table: {
            widths: [70, 400 , 90, 50, 70, 50],
            heights: [70, 20, 20, 250],
            body: [
              [
                {
                  image: 'data:image/png;base64,',
                  width:70,
                  height:70,
                  style:'logo'
                },
                {
                  colSpan:5,
                  text:this.name,
                  style:'title'
                },
                {},{},{},{}
              ],
              [
                {
                  text:'Objetivos',
                  style:'subheader'
                },
                {
                  text:this.objectives,
                  style:'paragraph'
                },
                {
                  text:'Dimensiones',
                  style:'subheader'
                },
                {
                  text:this.dimensions,
                  style:'paragraph2'
                },
                {
                  text:'Duración',
                  style:'subheader'
                },
                {
                  text:this.duration,
                  style:'paragraph2'
                }
              ],
              [
                {
                  image:this.createImage,
                  rowSpan:2,
                  colSpan:2,
                  width:470,
                  height:300,
                  style:'task'
                },
                {},
                {
                  text:'Descripción',
                  colSpan:4,
                  style:'description'
                },
                {},
                {},
                {}
              ],
              [
                {},
                {},
                {
                  text:this.description,
                  colSpan:4,
                  style:'paragraph'
                },
                {},
                {},
                {}
              ]
            ]
          },
          // Cambio de color de los bordes

          // layout: {
          //   hLineColor: function (i, node) {
          //     return (i === 0 || i === node.table.body.length) ? 'green' : 'red';
          //   },
          //   vLineColor: function (i, node) {
          //     return (i === 0 || i === node.table.widths.length) ? 'red' : 'red';
          //   },
          // }
        },
      ],
      styles: {
        title: {
          fontSize: 40,
          bold: true,
          color: '#FF0000',
          alignment: 'center',
          fillColor: '#000000',
          margin: [0, 10, 0, 0]

        },
        paragraph: {
          fontSize: 12,
          color: '#FFFFFF',
          alignment: 'justify',
          fillColor: '#FF0000',
          margin: [0, 2, 0, 0]
        },
        paragraph2: {
          fontSize: 12,
          color: '#FFFFFF',
          alignment: 'center',
          fillColor: '#FF0000',
          margin: [0, 2, 0, 0]
        },
        subheader: {
          fontSize: 15,
          alignment: 'center',
          fillColor: '#FF0000',
        },
        logo: {
          alignment: 'center',
          fillColor: '#000000',
        },
        task:{
          fillColor: '#FF0000',
        },
        description:{
          alignment: 'center',
          fontSize: 18,
          fillColor: '#FF0000',
        }
      }
    }

    const pdf = pdfMake.createPdf(pdfDefinition);
    pdf.open();

  }

}
